'use strict';

import React, { PropTypes } from 'react';

export default class ChatBtn extends React.Component{
	static propTypes = {
		onClick: PropTypes.func.isRequired,
		text: PropTypes.string,
	};
	static defaultProps = {
		text: '聊天'
	};
	render(){
		const { onClick,text } = this.props;
		const imgSrc='data:image/svg+xml;base64,';
		return (
			<span
				onClick={onClick}
				className="btn-chat"
			>
				<embed
					src={imgSrc} width="35" height="35"
					type="image/svg+xml"
					codebase="http://www.adobe.com/svg/viewer/install/"
				/>
				{text}
			</span>
		);
	}
}
